import React, { useRef, useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { ChatEngine } from 'react-chat-engine';
import { auth } from '../firebase';
import { useAuth } from '../contexts/AuthContext';
import { SendOutlined } from '@ant-design/icons';
import axios from 'axios';

const Chats = () => {
    const history = useHistory();
    const { user } = useAuth();
    const [loading, setLoading] = useState(true);

    const handleLogout = async () => {
        await auth.signOut();
        history.push('/'); //renavigate to login page
    }

    const getFile = async (url) => {
        const response = await fetch(url);
        const data = await response.blob();//containg our image

        return new File([data], "userPhoto.jpg", {type: 'image/jpeg'});
    }

    useEffect(() => {
        if(!user) {
            history.push('/');
            return;
        }

        /**
         * getting the already created user
         * if we have one we can immediately show the chat of that specific user
         */
        axios.get('https://api.chatengine.io/users/me', {
            headers: {
                'project-id': 'a2639f4a-0906-4ce1-b4be-965f13d279db',
                'user-name' : user.email,
                'user-secret' : user.uid
            }
        })
        .then(() => {
            setLoading(false);
        })
        .catch(() => {
            let formdata = new FormData();
            formdata.append('email', user.email);
            formdata.append('username', user.displayName);
            formdata.append('secret', user.uid);

            getFile(user.photoURL)
                .then((avatar) => {
                    formdata.append('avatar', avatar, avatar.name);
                    axios.post('https://api.chatengine.io/users', 
                        formdata,
                        { headers: { 'private-key': 'a9fc55e6-a71f-4626-94b0-5e443d2333e5'}}
                    )
                    .then(() => setLoading(false))
                    .catch((error) => console.log(error))
                })
        })
    }, [user, history]);
    return (
        <div className='chat-page'>
            <div className='nav-bar'>
                <div className='logo-tab'>
                    Arrowhead <SendOutlined />
                </div>
                <div onClick={handleLogout} className='logout-tab'>
                    Logout
                </div>
            </div>

            <ChatEngine
                height='calc(100vh - 66px)'
                projectId='a2639f4a-0906-4ce1-b4be-965f13d279db'
                userName={user.email}
                userSecret='.'
            />
        </div>
    )
}

export default Chats
