import React from 'react'
import { GoogleOutlined, FacebookOutlined } from '@ant-design/icons';
import "firebase/compat/app";
import { Link } from 'react-router-dom';

import { auth } from '../firebase';
import firebase from 'firebase/compat/app';

///
import { signInWithPopup, FacebookAuthProvider } from 'firebase/auth';
import './login.css';
///

/**
 * Login page with google and facebook auth provider buttons
 */
const Login = () => {

  const fbSignInHandler = () => {
    const provider = new FacebookAuthProvider();
    signInWithPopup(auth, provider)
    .then((re) => {
      console.log(re);
    })
    .catch((err) => {
      console.log(err.message);
    })
  }

  return (
    <div id='login-page'>
      <div className='heading'>
        <h2>Welcome to Arrowhead</h2>
      </div>
      <div id='login-card'>
        <h2>Sign Up</h2>

        <div className="form-container">
          <form action="">
            {true && 
              <div>
                <label htmlFor="name">Name</label>
                <input type="text" name='name'/>
              </div>}

            <div>
              <label htmlFor="email">Email</label>
              <input type="email" name='email'/>
            </div>

            <div>
              <label htmlFor="password">Password</label>
              <input type="password" name="password" />
            </div>

            <div class="box">
              <div class="js--image-preview"></div>
              <div class="upload-options">
                <label>
                  <input type="file" class="image-upload" accept="image/*" />
                </label>
              </div>
            </div>

            <div>
              <button className='signin-button' type="submit">Continue</button>
            </div>
          </form>

          <div class="social-divider">
            <span>or</span>
          </div>

          <div className='auth-signin-btn'>
            <div 
              className='login-button google'
              onClick={() => auth.signInWithRedirect(new firebase.auth.GoogleAuthProvider())}
            >
              <GoogleOutlined /> Google
            </div>

            <div 
              className='login-button facebook'
              onClick={fbSignInHandler}
            >
            {/* <div 
              className='login-button facebook'
              onClick={() => auth.signInWithRedirect(new firebase.auth.FacebookAuthProvider())}
            > */}
              <FacebookOutlined /> Facebook
            </div>
          </div>
        </div>
        <div>
          <span>You do have an account?<Link to="/chats">Login</Link></span>
        </div>
      </div>
    </div>
  );
}

export default Login
